package com.ada.web.service;import com.ada.common.PagingResult;import com.ada.model.AffConfigContract;import com.ada.web.dao.AffConfigContractDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;import java.util.Optional;@Servicepublic class AffConfigContractServiceImpl implements AffConfigContractService {    @Autowired    AffConfigContractDao affConfigContractDao;    @Override    public Optional<PagingResult> page(PagingResult page, String name, String mobilePartner, Long status, String fromDate, String toDate) {        return affConfigContractDao.page(page, name, mobilePartner, status, fromDate, toDate);    }    @Override    public Long add(AffConfigContract affConfigContract) {        return affConfigContractDao.add(affConfigContract);    }    @Override    public AffConfigContract getDetail(Long id) {        return affConfigContractDao.getDetail(id);    }    @Override    public Boolean edit(AffConfigContract affConfigContract) {        boolean isUpdate = affConfigContractDao.edit(affConfigContract);        if (isUpdate) return true;        return false;    }    @Override    public Optional<PagingResult> getPartnerConfig(PagingResult page, String mobilePartner, String fromDate, String toDate, String nameConfig) {        return affConfigContractDao.getPartnerConfig(page, mobilePartner, fromDate, toDate, nameConfig);    }    @Override    public List<HashMap<String, String>> getNameConfig(String mobilePartner) {        return affConfigContractDao.getNameConfig(mobilePartner);    }}