package com.ada.web.service;import com.ada.common.PagingResult;import com.ada.web.dao.BalanceHistoryDAOImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Optional;@Servicepublic class BalanceHistoryServiceImpl implements BalanceHistoryService {    @Autowired    private BalanceHistoryDAOImpl historyDAO;    @Override    public Long getBalanceWithdraw(String username) {        return historyDAO.getBalanceWithdraw(username);    }    @Override    public Optional<PagingResult> page(PagingResult page, String username, String itemName,Long transType, String fromGenDate, String toGenDate) {        return historyDAO.page(page, username, itemName,transType, fromGenDate, toGenDate);    }}