package com.ada.web.service.chonsoApi;import com.google.gson.Gson;import com.ada.common.Config;import com.ada.model.api.request.BalanceHistoryRequest;import com.ada.model.api.response.BalanceHistoryResponse;import com.ada.web.service.APIService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;@Servicepublic class BalanceHistoryServiceAPI {    private Gson gson = new Gson();    private static final Logger logger = LoggerFactory.getLogger(BalanceHistoryServiceAPI.class);    @Autowired    private APIService apiService;    public BalanceHistoryResponse getBalance(BalanceHistoryRequest request, String token) {        BalanceHistoryResponse response = null;        try {            String result = apiService.callAPI(Config.api_ctvosp_balance_his, new HashMap<>(), gson.toJson(request), token);            if (result != null && !result.equals("")) {                response = gson.fromJson(result, BalanceHistoryResponse.class);                logger.info("GET BALANCE-HISTORY SUCCESS: " + response);            }        } catch (Exception e) {            logger.error("GET BALANCE-HISTORY BY ERROR: " + e.getMessage());            e.printStackTrace();        }        return response;    }}