package com.ada.web.service.chonsoApi;import com.google.gson.Gson;import com.ada.common.Config;import com.ada.model.api.request.PlaceRequest;import com.ada.model.api.response.PlaceResponse;import com.ada.web.service.APIService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;@Servicepublic class PalaceService {    private Gson gson = new Gson();    private static final Logger logger = LoggerFactory.getLogger(PalaceService.class);    @Autowired    private APIService apiService;    public PlaceResponse places(PlaceRequest request, String token) {        PlaceResponse response = null;        try {            String result = apiService.callAPICtv(Config.api_ctvosp_common_place, new HashMap<>(), gson.toJson(request), token);            System.out.println("REQUEST GET PALACE : " + gson.toJson(request));            if (result != null && !result.equals("")) {                response = gson.fromJson(result, PlaceResponse.class);            }        } catch (Exception e) {            System.out.println(String.format("Get PALACE-ERROR: %s ", e.getMessage()));            logger.error(String.format("Get PALACE-ERROR: %s ", e.getMessage()));        }        return response;    }}